CDN

  &lt;script type="module"&gt;
  // Auto registers as &lt;light-pen&gt;
  import "https://cdn.jsdelivr.net/npm/light-pen/exports/light-pen-register.js"

  // Manual Register
  import LightPen from "https://cdn.jsdelivr.net/npm/light-pen/exports/light-pen.js/exports/light-pen.js"
  LightPen.define()
  // =&gt; Registers as &lt;light-pen&gt;
&lt;/script&gt;



